const fs = require('fs');
const path = require('path');

// Create a minimal black JPEG image (base64 encoded)
const blackImageBase64 = '';

// Convert base64 to buffer and save as JPEG
const imageBuffer = Buffer.from(blackImageBase64, 'base64');
const outputPath = path.join(__dirname, 'test-stable-image.jpg');

fs.writeFileSync(outputPath, imageBuffer);
console.log('Test image created at:', outputPath);